CREATE TABLE snipe
(
    message_id BIGINT NOT NULL,
    victim_id     BIGINT NOT NULL,
    FOREIGN KEY (message_id) REFERENCES message (message_id),
    PRIMARY KEY (message_id, victim_id)
);

